import {Injectable} from '@angular/core';
import {Recipe} from '../model/recipe';
import {Subject} from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class RecipeService {

  recipesChanged = new Subject<Recipe[]>();

  recipes: Recipe[] = [{
    id: 1,
    name: 'Recipe1',
    description: 'description1',
    imagePath: 'data:image/jpeg;base64,',
    ingredients: []
  },
    {
      id: 2,
      name: 'Recipe2',
      description: 'description2',
      imagePath: 'data:image/jpeg;base64,',
      ingredients: []
    },
    {
      id: 3,
      name: 'Recipe3',
      description: 'description3',
      imagePath: 'https://hips.hearstapps.com/hmg-prod.s3.amazonaws.com/images/healthy-dinner-ideas-tofu-bowl-1574613204.jpg?crop=0.811xw:0.541xh;0.0737xw,0.247xh&resize=640:*',
      ingredients: []
    },
    {
      id: 4,
      name: 'Recipe4',
      description: 'description4',
      imagePath: 'https://cookieandkate.com/images/2017/05/vegetable-fried-rice.jpg',
      ingredients: []
    }];

  constructor() {
  }

  public getRecipes() {
    return this.recipes;
  }

  createNewRecipe(recipe: Recipe) {
    const size = this.recipes.length;
    recipe.id = size + 1;
    this.recipes.push(recipe);
  }

  getRecipeById(id: number): Recipe {
    return this.recipes.filter(recipe => recipe.id === id)[0];
  }

  deleteRecipe(id: number) {
    this.recipes = this.recipes.filter(recipe => recipe.id !== id);
    this.recipesChanged.next(this.recipes);
  }
}
